function randomWord() {
    return words[Math.floor(Math.random() * words.length)];
}

function maybe(probability) {
    return Math.random() < probability;
}

function wrap(text, tag) {
    return `<${tag}>${text}</${tag}>`;
}

function decorate(word) {
    let w = word;
    if (maybe(0.01)) w = wrap(w, 'b');
    if (maybe(0.01)) w = wrap(w, 'i');
    if (maybe(0.01)) w = wrap(w, 'u');
    if (maybe(0.01)) w = wrap(w.toUpperCase(), 'span class="allcaps"');
    if (maybe(0.01)) w = Math.floor(Math.random() * 1e12).toString().slice(0, Math.floor(Math.random() * 12) + 1);
    if (maybe(0.02)) w = `(${w})`;
    if (maybe(0.005)) w = `[${w}]`;
    if (maybe(0.005)) w = `{${w}}`;
    if (maybe(0.001)) w = `&lt;${w}&gt;`;
    if (maybe(0.06)) w += ",";
    if (maybe(0.01)) w += ":";
    if (maybe(0.01)) w += ";";
    if (maybe(0.01)) w += emojis[Math.floor(Math.random() * emojis.length)];
    return w;
}

function generateOutput() {
      const input = document.getElementById("input").value.trim();
      const outputDiv = document.getElementById("output");

      if (input === "") {
        outputDiv.innerHTML = "";
        return;
      }

      if (input === "<em>Hi</em>") {
        outputDiv.innerHTML = "The HORSE is a noble animal.";
        return;
      }

      let wordCount = Math.floor(Math.random() * 60) + 1;
      let wordsList = [];
      let capitalizeNext = maybe(0.5);

      for (let i = 0; i < wordCount; i++) {
        let word = randomWord();
        if (capitalizeNext) {
          word = word.charAt(0).toUpperCase() + word.slice(1);
          capitalizeNext = false;
        }
        let decorated = decorate(word);
        if (maybe(0.02)) {
          decorated += ".";
          capitalizeNext = true;
        }
        wordsList.push(decorated);
      }

      // Final punctuation
      if (!capitalizeNext) {
        let punctuationChance = Math.random();
        if (punctuationChance < 0.7) wordsList[wordsList.length - 1] += ".";
        else if (punctuationChance < 0.9) wordsList[wordsList.length - 1] += "?";
        else wordsList[wordsList.length - 1] += "!";
      }

      outputDiv.innerHTML = wordsList.join(" ");
    }

    document.getElementById("input").addEventListener("input", generateOutput);